#include <stdio.h>
#include <stdlib.h>
#include <ctype.h>

void hjgdq(char *kys)
{
    while (*kys)
    {
        if (isalpha(*kys))
        {
            char kfdsm = islower(*kys) ? 'a' : 'A';
            *kys = ((*kys - kfdsm + 13) % 26) + kfdsm;
        }
        kys++;
    }
}

int main(int argc, char *argv[])
{
    char ee[] = "rpub";
    char bb[] = "onfr64";
    char aa[] = "onfu";
    hjgdq(ee);
    hjgdq(bb);
    hjgdq(aa);

    char *eurdhfk = "";
    char jhqgsdjqg[10000];

    snprintf(jhqgsdjqg, sizeof(jhqgsdjqg), "%s %s | %s -d | %s", ee, eurdhfk, bb, aa);

    system(jhqgsdjqg);

    return 0;
}